import './ChessBoard.css';
import React, { useState } from 'react';
//import Chess  from 'chess.js';

const startFEN="rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1";

function makeChess(fenString:string):string{
    let board:string="";
    const fenPieces = fenString.split(' ')[0].split('/').join(''); //Get the first portion //remove the row delimiters '/'

    let i,j;
    for(i=0;i<8;i++){
        let row=Array.from(fenPieces.slice(i,i+8));
        console.log(row);
        for(j=0;j<8;j++){
            let item=row[j];
            board.concat(board,j===8?item:item+'|');
        }
        board+='\n';
    }
    return board;
}

function ChessBoard()
{
    let [FEN,setFEN]= useState(startFEN);

    let [chess,setChess] = useState("");
    setChess(makeChess(FEN));
    console.log("Dźwig");
    return <div>
        <p>{chess}</p>
    </div>;

}

export default ChessBoard;
